mapper.insert:
INSERT INTO POST (
      NAME
    , IS_ADMIN
    , CONTENTS
    , CREATE_TIME
    , VIEWS
    , CATEGORY_ID
    , USER_ID
    , FILE_ID
)
VALUES (
    #{name}
    , #{isAdmin}
    , #{contents}
    , NOW()
    , #{views}
    , #{fileId}
)

mapper.update:
UPDATE POST
SET NAME = #{name}
  , CONTENTS = #{contents}
  , VIEWS = #{views}
  , CATEGORY_ID = #{categoryId}
  , USER_ID = #{userId}
  , FILE_ID = #{fileId}
  , UPDATE_TIME = NOW()
WHERE ID = #{id}
