mapper.insert:
INSERT INTO USER (
                  USER_ID
                  , PASSWORD
                  , NICKNAME
                  , IS_WITH_DRAW
                  , STATUS
                  , IS_ADMIN
)
VALUES (
      #{userId}
    , #{password}
    , #{nickname}
    , #{isWithDraw}
    , #{status}
    , #{isAdmin}          
)

mapper.update:
UPDATE USER
SET PASSWORD = #{password}
  , NICKNAME = #{nickname}
  , IS_WITH_DRAW = #{isWithDraw}
  , STATUS = #{status}
WHERE ID = #{userId}

mapper.select:
SELECT COUNT(ID)
FROM USER
WHERE USER_ID = #{userId}

mapper.update:
UPDATE USER
SET PASSWORD = #{password}
WHERE USER_ID = #{userId}
